

package javaStuff;

import java.util.Date;


public class DataDemoPrintf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Date date=new Date();
		System.out.printf("%tc%n",date);
		System.out.printf("%tF%n", date);
		System.out.printf("%tr%n", date);

	}

}
